mapper.select:
SELECT 

FROM os_comment_reply a

WHERE a.id = #{id}

SELECT 

FROM os_comment_reply a




		AND a.content LIKE 
			'%'||#{content}||'%'
			'%'+#{content}+'%'
			concat('%',#{content},'%')


		AND a.status = #{status}


		AND a.type = #{type}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
