mapper.select:
SELECT 

FROM os_customer_bill a

WHERE a.id = #{id}

SELECT 

FROM os_customer_bill a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type = #{type}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM os_customer_bill a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE os_customer_bill SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
