mapper.select:
SELECT 

FROM os_order_product a

WHERE a.id = #{id}

SELECT
FROM os_order_product a
AND a.order_id = #{order.id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM os_order_product a






		ORDER BY ${page.orderBy}

mapper.update:
