mapper.select:
SELECT

FROM os_product_design a

WHERE a.id = #{id}

SELECT

FROM os_product_design a

WHERE a.product_id = #{id}

SELECT

FROM os_product_design a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.name LIKE
        '%'||#{name}||'%'
        '%'+#{name}+'%'
        concat('%',#{name},'%')


        AND a.code = #{code}


        AND a.customer_id = #{customer.id}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM os_product_design a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE os_product_design SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
